import 'bootstrap/dist/css/bootstrap.min.css';
import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import DisplayList from './DisplayList.jsx';

export default class FilteredList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        list: this.props.list,
        size: "AllSize",
        type:"AllType",
      };
    }

    onSelectFilterSize = event => {
        this.setState({
            size: event
        });
    }
    onSelectFilterType = event => {
        this.setState({
            type: event
        });
    }
    matchesFilterSize = item => {
        if (this.state.size === "AllSize") {
            return true;
        } else if (this.state.size === item.size) {
            return true;
        } else {
            return false;
        };
    };
    matchesFilterType = item => {
        if (this.state.type === "AllType") {
            return true;
        } else if (this.state.type === item.type) {
            return true;
        } else {
            return false;
        };
    };

    matchesAllFilter = item => {
        if (this.matchesFilterType(item) && his.matchesFilterSize(item)) {
            return true;
        } else {
            return false;
        } 
    }


    render() {
        console.log(this.state.size)
        console.log(this.state.type)
    return (
        <div>
            <Navbar bg="light" expand="lg">
            <Nav className="mr-auto">
            <Navbar.Brand >Size: </Navbar.Brand> 
                <Nav.Item>
                    <Nav.Link eventKey="AllSize" onSelect={this.onSelectFilterSize}> All
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Desktop" onSelect={this.onSelectFilterSize}> Desktop
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterSize}> Medium
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Large" onSelect={this.onSelectFilterSize}> Large
                    </Nav.Link>
                </Nav.Item>
            </Nav>
            </Navbar>
            <Navbar bg="light" expand="lg">
            <Nav className="mr-auto">
            <Navbar.Brand >Type: </Navbar.Brand> 
                <Nav.Item>
                    <Nav.Link eventKey="AllType" onSelect={this.onSelectFilterType}> All
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="One" onSelect={this.onSelectFilterType}> One
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Two" onSelect={this.onSelectFilterType}> Two
                    </Nav.Link>
                </Nav.Item>
            </Nav>
            </Navbar>
        <DisplayList list={this.props.list.filter(this.matchesAllFilter)} />
        </div>
        )};
  };
