import './ShoppingCart.css';
import React from "react";
import { Card, ListGroup, Button} from "react-bootstrap";
import Grid from "@material-ui/core/Grid";

export default class ShoppingCart extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        list: this.props.list,
        total: 0.0,
      };
      this.totalCost = this.totalCost.bind(this)
    }

    totalCost() {
      const reducer = (accumulator, currentItem) => accumulator + currentItem.price;
      const newTotal = this.props.list.reduce(reducer);
      this.setState({total: newTotal})
    }

    render() {
        return (
        <div>

            <Card>
            <Card.Header as="h5">Shopping Cart</Card.Header>
            <Card.Body>
              <Grid container justify="center" spacing={3}>
              {this.props.list.map(item => (
              <Grid key={item.name} item>
                <Card style={{ width: '18rem' }}>
                  <Card.Img variant="top" src={item.image} />
                  <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  </Card.Body>
                  <ListGroup className="list-group-flush">
                    <ListGroup.Item>Size: {item.size}</ListGroup.Item>
                    <ListGroup.Item>Shape: {item.shape}</ListGroup.Item>
                    <ListGroup.Item>Cost: ${item.price}</ListGroup.Item>
                  </ListGroup>
                  <Card.Body>
                <Button variant="primary" value={item} onClick={() => this.addItem(item)}> Add to Cart</Button>
                </Card.Body>
                </Card>
              </Grid>
          ))}
              </Grid>
            </Card.Body>
            <Card.Footer><p id="totalCost">Total: ${this.totalCost}</p><Button id="checkoutButton">Checkout</Button></Card.Footer>
            </Card>
        </div>
        )};
  };
