import React from "react";
import { Nav, Navbar } from "react-bootstrap";
import DisplayList from './DisplayList.jsx';

export default class FilteredList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        list: this.props.list,
        size: "All",
      };
    }

    onSelectFilterSize = event => {
        this.setState({
            size: event
        });
    }
    matchesFilterSize = item => {
        if (this.state.size === "All") {
            return true;
        } else if (this.state.size === item.size) {
            return true;
        } else {
            return false;
        };
    };

    render() {
    return (
        <div>
            <Navbar bg="light" expand="lg">
            <Nav className="mr-auto">
                <Nav.Item>
                    <Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}> All
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Desktop" onSelect={this.onSelectFilterSize}> Desktop
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterSize}> Medium
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Large" onSelect={this.onSelectFilterSize}> Large
                    </Nav.Link>
                </Nav.Item>
            </Nav>
            </Navbar>
        <DisplayList list={this.props.list.filter(this.matchesFilterSize)} />
        </div>
        )};
  };
