import './App.css';
import React from "react";
import FilteredList from './FilteredList.jsx';
import ShoppingCart from "./ShoppingCart.jsx";
import productList from "./data.jsx";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currData: productList,
      // list of dicts where each dict has key that is the item 
      // and val indicating the count of that item
      shoppingCart: [],
    };
  }

  addItem = item => {
    console.log("I see a new item")
    const sameItems = this.state.shoppingCart.filter(k => item === k.name);
    if (sameItems.length >= 1) {
      console.log("This item is already in the cart")
      for (var currItem in this.state.shoppingCart) {
        if (currItem.key.name === item.name) {
          var currVal = Number(currItem[currItem.key]);
          console.log("Increasing by 1")
          currItem[currItem] = currVal + 1;
        }
      }
    } else {
      console.log("This item is going to be newly added")
      var newItem = {}
      newItem[item.name] = 1;
      const shoppingCart = [...this.state.shoppingCart, item];
    this.setState({shoppingCart});
    }

  }

  removeItem = item => {
    const newShoppingCart = this.state.ShoppingCart.filter(k => item != k.key)
    this.setState({shoppingCart: newShoppingCart})
  }
  render() {
    console.log(productList)
  return (
      <div className="App">
        <h1>Art Shop</h1>
        <div className="flex-container">
          <div className="flex-child">
            <FilteredList list={this.state.currData} addItem={this.addItem}/>
          </div>
          <div className="flex-child">
            <ShoppingCart list={this.state.shoppingCart} />
            </div>
          </div>
        </div>
  )}
}
