import './App.css';
import React from "react";
import FilteredList from './FilteredList.jsx';
import ShoppingCart from "./ShoppingCart.jsx";
import productList from "./data.jsx";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currData: productList,
      shoppingCart: productList.map(p => ({ product: p, quantity: 0 })),
    };
  }

  addItem = item => {
      const newShoppingCart = [...this.state.shoppingCart];
      const currQuant = Number(newShoppingCart[item]);
      newShoppingCart[item] = currQuant + 1;
      console.log(newShoppingCart)
      this.setState({shoppingCart: newShoppingCart});
  }

  removeItem = item => {
    const newShoppingCart = [...this.state.shoppingCart];
    newShoppingCart[item] = 0;
    this.setState({shoppingCart: newShoppingCart});
  }

  handleDecrease = item => {
    const newShoppingCart = [...this.state.shoppingCart];
    const currQuant = Number(newShoppingCart[item]);
    if (currQuant <= 0) {
      newShoppingCart[item] = 0;
    } else {
      newShoppingCart[item] = currQuant - 1;
    }
    this.setState({shoppingCart: newShoppingCart});  
  }

  render() {
    console.log(productList)
  return (
      <div className="App">
        <h1>Art Shop</h1>
        <div className="flex-container">
          <div className="flex-child">
            <FilteredList list={this.state.currData} addItem={this.addItem}/>
          </div>
          <div className="flex-child">
            <ShoppingCart list={this.state.shoppingCart} />
            </div>
          </div>
        </div>
  )}
}
