import 'bootstrap/dist/css/bootstrap.min.css';
import React from "react";
import { Nav, Navbar, NavDropdown } from "react-bootstrap";
import DisplayList from './DisplayList.jsx';

export default class FilteredList extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        list: this.props.list,
        originalList: [...this.props.list],
        size: "AllSize",
        shape:"AllShape",
        price:"Select",
      };
    }

    onSelectFilterSize = event => {
        this.setState({
            size: event
        });
    }
    onSelectFilterShape = event => {
        this.setState({
            shape: event
        });
    }
    matchesFilterSize = item => {
        if (this.state.size === "AllSize") {
            return true;
        } else if (this.state.size === item.size) {
            return true;
        } else {
            return false;
        };
    };
    matchesFilterShape = item => {
        if (this.state.shape === "AllShape") {
            return true;
        } else if (this.state.shape === item.shape) {
            return true;
        } else {
            return false;
        };
    };

    lowToHigh = (a, b) => {
        const aCost = Number(a.price);
        const bCost = Number(b.price);
        return aCost - bCost
    };

    highToLow = (a, b) => {
        const aCost = Number(a.price);
        const bCost = Number(b.price);
        return bCost - aCost
    };

    priceSort() {
        if (this.state.price === "All") {
            return this.originalList
        } else if (this.state.price === "Lowest to Highest"){
                this.state.list.sort(this.lowToHigh)
        } else if (this.state.price === "Highest to Lowest"){
                list: this.state.list.sort(this.highToLow)
        }
    }

    onSelectFilterPrice = event => {
        this.setState({
            price: event
        });
    }

    matchesAllFilter = item => {
        if (this.matchesFilterShape(item) && this.matchesFilterSize(item)) {
            return true;
        } else {
            return false;
        } 
    }

    render() {
    return (
        <div>
            <div id="meta-nav-bar">
            <Navbar bg="light" expand="lg">
            <Nav className="mr-auto">
            <Navbar.Brand >Size: </Navbar.Brand> 
                <Nav.Item>
                    <Nav.Link eventKey="AllSize" onSelect={this.onSelectFilterSize}> All
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Small" onSelect={this.onSelectFilterSize}> Small
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Medium" onSelect={this.onSelectFilterSize}> Medium
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Large" onSelect={this.onSelectFilterSize}> Large
                    </Nav.Link>
                </Nav.Item>
            </Nav>
            </Navbar>
            <Navbar bg="light" expand="lg">
            <Nav className="mr-auto">
            <Navbar.Brand >Shape: </Navbar.Brand> 
                <Nav.Item>
                    <Nav.Link eventKey="AllShape" onSelect={this.onSelectFilterShape}> All
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Landscape" onSelect={this.onSelectFilterShape}> Landscape
                    </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                    <Nav.Link eventKey="Portrait" onSelect={this.onSelectFilterShape}> Portrait
                    </Nav.Link>
                </Nav.Item>
            </Nav>
            </Navbar>
            <Navbar bg="light" expand="lg">
            <Navbar.Brand >Price: </Navbar.Brand> 
            <NavDropdown title={this.state.price} id="basic-nav-dropdown">
                <NavDropdown.Item eventKey="Select" onSelect={this.onSelectFilterPrice} >Select</NavDropdown.Item>
                <NavDropdown.Item eventKey="Lowest to Highest" onSelect={this.onSelectFilterPrice}>Lowest to Highest</NavDropdown.Item>
                <NavDropdown.Item eventKey="Highest to Lowest" onSelect={this.onSelectFilterPrice}>Highest to Lowest</NavDropdown.Item>
            </NavDropdown>
            </Navbar>
            </div>
        <div id="product-list-display">
        {this.priceSort()}
        <DisplayList list={this.state.list.filter(this.matchesAllFilter)} />
        </div>
        </div>
        )};
  };
